({ 
  logo1Src,
  logo1Alt, 
  navLink1Text, 
  navLink1Href,
  navLink2Text,
  navLink2Href,
  navLink3Text, 
  navLink3Href,
  navLink4Text,
  navLink4Href,
  ctaButtonText,
  ctaButtonHref
}) => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  return (
    <header className="w-full bg-[#F5F5F5] shadow-sm">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <a href="/" className="flex items-center">
          <img 
            src={logo1Src} 
            alt={logo1Alt} 
            className="h-10 w-auto" 
          />
        </a>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center space-x-6">
          <a 
            href={navLink1Href} 
            className="text-[#8B4513] hover:text-[#C41E3A] transition-colors duration-300"
          >
            {navLink1Text}
          </a>
          <a 
            href={navLink2Href} 
            className="text-[#8B4513] hover:text-[#C41E3A] transition-colors duration-300"
          >
            {navLink2Text}
          </a>
          <a 
            href={navLink3Href} 
            className="text-[#8B4513] hover:text-[#C41E3A] transition-colors duration-300"
          >
            {navLink3Text}
          </a>
          <a 
            href={navLink4Href} 
            className="text-[#8B4513] hover:text-[#C41E3A] transition-colors duration-300"
          >
            {navLink4Text}
          </a>
        </nav>

        {/* CTA Button */}
        <div className="hidden md:block">
          <a 
            href={ctaButtonHref}
            className="bg-[#8B4513] text-white px-4 py-2 rounded-md hover:bg-[#C41E3A] transition-colors duration-300"
          >
            {ctaButtonText}
          </a>
        </div>

        {/* Mobile Menu Toggle */}
        <div className="md:hidden">
          <button 
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="text-[#8B4513] focus:outline-none"
            aria-label="Toggle mobile menu"
          >
            <Icon 
              icon={isMenuOpen ? "mdi:close" : "mdi:menu"} 
              width={24} 
              height={24} 
            />
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-[#F5F5F5] absolute w-full z-50">
          <div className="px-4 pt-2 pb-4 space-y-3">
            <a 
              href={navLink1Href} 
              className="block text-[#8B4513] py-2 border-b border-[#D2B48C]"
            >
              {navLink1Text}
            </a>
            <a 
              href={navLink2Href} 
              className="block text-[#8B4513] py-2 border-b border-[#D2B48C]"
            >
              {navLink2Text}
            </a>
            <a 
              href={navLink3Href} 
              className="block text-[#8B4513] py-2 border-b border-[#D2B48C]"
            >
              {navLink3Text}
            </a>
            <a 
              href={navLink4Href} 
              className="block text-[#8B4513] py-2 border-b border-[#D2B48C]"
            >
              {navLink4Text}
            </a>
            <a 
              href={ctaButtonHref}
              className="block bg-[#8B4513] text-white text-center px-4 py-2 rounded-md hover:bg-[#C41E3A] transition-colors duration-300"
            >
              {ctaButtonText}
            </a>
          </div>
        </div>
      )}
    </header>
  );
};

({ 
  headline1Text = 'Discover Extraordinary Fragrances', 
  subheadline1Text = 'Curated Luxury Perfumes for the Discerning Collector',
  cta1Text = 'Explore Our Collection',
  cta1Href = '/collection',
  heroImage1Src = 'https://scent-secrets-cdn.com/hero-main.jpg',
  heroImage1Alt = 'Luxurious Fragrance Collection by Scent Secrets'
}) => {
  const variants = {
    initial: { opacity: 0, y: 20 },
    animate: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        duration: 0.6, 
        ease: [0.4, 0, 0.2, 1] 
      } 
    }
  };

  return (
    <section className="w-full relative flex flex-col lg:flex-row min-h-[80vh] bg-neutral-50 dark:bg-neutral-900">
      <div className="container mx-auto px-4 lg:px-8 flex flex-col lg:flex-row items-center justify-between py-16 space-y-8 lg:space-y-0 lg:space-x-12">
        {/* Text Content */}
        <motion.div 
          initial="initial"
          animate="animate"
          variants={variants}
          className="w-full lg:w-1/2 flex flex-col justify-center space-y-6"
        >
          <h1 className="text-4xl lg:text-5xl font-playfair font-bold text-mahogany-800 dark:text-neutral-100 tracking-tight leading-tight">
            {headline1Text}
          </h1>
          <p className="text-lg lg:text-xl font-lato text-neutral-700 dark:text-neutral-300 leading-relaxed">
            {subheadline1Text}
          </p>
          <motion.a
            href={cta1Href}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="inline-block px-6 py-3 bg-mahogany-600 text-white font-lato font-semibold rounded-lg shadow-md hover:bg-mahogany-700 transition-colors duration-300 text-center"
          >
            {cta1Text}
          </motion.a>
        </motion.div>

        {/* Hero Image */}
        <motion.div 
          initial="initial"
          animate="animate"
          variants={{
            ...variants,
            animate: {
              ...variants.animate,
              transition: { 
                ...variants.animate.transition, 
                delay: 0.3 
              }
            }
          }}
          className="w-full lg:w-1/2 flex items-center justify-center"
        >
          <div className="w-full max-w-xl aspect-[4/3] rounded-xl overflow-hidden shadow-2xl">
            <img 
              src={heroImage1Src}
              alt={heroImage1Alt}
              className="w-full h-full object-cover object-center transition-transform duration-500 hover:scale-105"
            />
          </div>
        </motion.div>
      </div>
    </section>
  );
};

({
  title1 = 'Our Unique Fragrance Features',
  subtitle1 = 'Discover the essence of luxury in every scent',
  feature1Title = 'Curated Selection',
  feature1Description = 'Handpicked fragrances from the world\'s most exclusive perfumeries',
  feature2Title = 'Vintage Collections',
  feature2Description = 'Rare and timeless scents that tell a remarkable story',
  feature3Title = 'Niche Parfumes',
  feature3Description = 'Exquisite, small-batch fragrances you won\'t find anywhere else',
  feature1IconName = 'mdi:star-outline',
  feature2IconName = 'mdi:clock-time-eight-outline',
  feature3IconName = 'mdi:diamond-outline'
}) => {
  const features = [
    {
      icon: feature1IconName,
      title: feature1Title,
      description: feature1Description
    },
    {
      icon: feature2IconName,
      title: feature2Title,
      description: feature2Description
    },
    {
      icon: feature3IconName,
      title: feature3Title,
      description: feature3Description
    }
  ];

  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="font-playfair text-3xl md:text-4xl text-[#8B4513] mb-4">
          {title1}
        </h2>
        <p className="font-lato text-lg text-[#1A1A1A] mb-12">
          {subtitle1}
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <div 
              key={index} 
              className="bg-white rounded-lg shadow-md p-6 transform transition duration-300 hover:scale-105 hover:shadow-lg"
            >
              <div className="flex justify-center mb-4">
                <Icon 
                  icon={feature.icon} 
                  className="w-12 h-12 text-[#8B4513]" 
                />
              </div>
              <h3 className="font-playfair text-xl text-[#8B4513] mb-3">
                {feature.title}
              </h3>
              <p className="font-lato text-[#2C2C2C]">
                {feature.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

({ 
  testimonial1Text, 
  testimonial1AuthorName, 
  testimonial1AuthorTitle, 
  testimonial2Text, 
  testimonial2AuthorName, 
  testimonial2AuthorTitle,
  testimonial3Text, 
  testimonial3AuthorName, 
  testimonial3AuthorTitle 
}) => {
  const [activeSlide, setActiveSlide] = React.useState(0);
  const testimonials = [
    {
      text: testimonial1Text,
      author: testimonial1AuthorName,
      title: testimonial1AuthorTitle
    },
    {
      text: testimonial2Text,
      author: testimonial2AuthorName,
      title: testimonial2AuthorTitle
    },
    {
      text: testimonial3Text,
      author: testimonial3AuthorName,
      title: testimonial3AuthorTitle
    }
  ];

  const handleNextSlide = () => {
    setActiveSlide((prev) => (prev + 1) % testimonials.length);
  };

  const handlePrevSlide = () => {
    setActiveSlide((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4 md:px-12 lg:px-24">
      <div className="max-w-4xl mx-auto text-center">
        <div className="relative">
          <motion.div
            key={activeSlide}
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -100 }}
            transition={{ duration: 0.5 }}
            className="mb-8"
          >
            <Icon 
              icon="mdi:quote-open" 
              className="mx-auto text-[#8B4513] text-5xl mb-4" 
            />
            <p className="text-xl md:text-2xl text-[#1A1A1A] font-lato font-light italic mb-6">
              {testimonials[activeSlide].text}
            </p>
            <div className="mt-4">
              <h4 className="text-xl font-playfair font-bold text-[#8B4513]">
                {testimonials[activeSlide].author}
              </h4>
              <p className="text-base text-[#2C2C2C] font-lato">
                {testimonials[activeSlide].title}
              </p>
            </div>
          </motion.div>

          <div className="flex justify-center items-center mt-8 space-x-4">
            <button 
              onClick={handlePrevSlide}
              aria-label="Previous Testimonial"
              className="text-[#8B4513] hover:text-[#D2B48C] transition-colors duration-300"
            >
              <Icon icon="mdi:chevron-left" className="text-3xl" />
            </button>
            <div className="flex space-x-2">
              {testimonials.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setActiveSlide(index)}
                  className={`w-3 h-3 rounded-full ${
                    activeSlide === index 
                      ? 'bg-[#8B4513]' 
                      : 'bg-[#D2B48C] opacity-50'
                  } transition-all duration-300`}
                />
              ))}
            </div>
            <button 
              onClick={handleNextSlide}
              aria-label="Next Testimonial"
              className="text-[#8B4513] hover:text-[#D2B48C] transition-colors duration-300"
            >
              <Icon icon="mdi:chevron-right" className="text-3xl" />
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

({ 
  galleryItems1ImageSrc, 
  galleryItems1Title, 
  galleryItems1Description,
  galleryItems2ImageSrc, 
  galleryItems2Title, 
  galleryItems2Description,
  galleryItems3ImageSrc, 
  galleryItems3Title, 
  galleryItems3Description,
  sectionTitle,
  sectionSubtitle 
}) => {
  const [activeItem, setActiveItem] = React.useState(0);

  const galleryItems = [
    {
      imageSrc: galleryItems1ImageSrc,
      title: galleryItems1Title,
      description: galleryItems1Description
    },
    {
      imageSrc: galleryItems2ImageSrc,
      title: galleryItems2Title,
      description: galleryItems2Description
    },
    {
      imageSrc: galleryItems3ImageSrc,
      title: galleryItems3Title,
      description: galleryItems3Description
    }
  ];

  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4 md:px-12 lg:px-24">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h2 className="font-playfair text-4xl text-[#8B4513] mb-4">
            {sectionTitle}
          </h2>
          <p className="font-lato text-lg text-[#1A1A1A] max-w-2xl mx-auto">
            {sectionSubtitle}
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-8">
          {galleryItems.map((item, index) => (
            <React.Fragment key={index}>
              <motion.div 
                initial={{ opacity: 0, scale: 0.95 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ 
                  duration: 0.5, 
                  ease: [0.4, 0, 0.2, 1] 
                }}
                viewport={{ once: true }}
                className="bg-white rounded-xl shadow-lg overflow-hidden group hover:shadow-xl transition-all duration-300"
                onClick={() => setActiveItem(index)}
              >
                <div className="relative overflow-hidden">
                  <img 
                    src={item.imageSrc} 
                    alt={item.title}
                    className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-105"
                  />
                </div>
                <div className="p-6">
                  <h3 className="font-playfair text-2xl text-[#8B4513] mb-2">
                    {item.title}
                  </h3>
                  <p className="font-lato text-base text-[#2C2C2C]">
                    {item.description}
                  </p>
                </div>
              </motion.div>
            </React.Fragment>
          ))}
        </div>
      </div>
    </section>
  );
};

