  copyright1Text = '© 2024 Scent Secrets. All Rights Reserved.',
  newsletter1Placeholder = 'Enter your email',
  newsletter1ButtonText = 'Subscribe',
  socialLink1Href = 'https://instagram.com/scentsecrets',
  socialLink2Href = 'https://facebook.com/scentsecrets',
