  const quickLinks = [
    { text: footerLink1Text, href: '/shop' },
    { text: footerLink2Text, href: '/about' },
    { text: footerLink3Text, href: '/contact' },
    { text: footerLink4Text, href: '/faq' },
    { text: footerLink5Text, href: '/shipping' },
    { text: footerLink6Text, href: '/returns' }
  ];

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement newsletter signup logic
    console.log('Newsletter signup:', email);
    setEmail('');
  };

  return (
    <footer className="w-full bg-[#1A1A1A] text-[#F5F5F5] py-12 px-4 md:px-8 lg:px-16">
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Company Info */}
        <div className="space-y-4">
          <h3 className="text-xl font-bold font-playfair text-[#D2B48C]">{companyName1Text}</h3>
          <p className="text-sm font-lato opacity-80">
            Discover our carefully curated selection of the finest luxury fragrances available today.
          </p>
        </div>

        {/* Quick Links */}
        <div className="space-y-4">
          <h4 className="text-lg font-bold font-playfair text-[#D2B48C]">Quick Links</h4>
          <div className="grid grid-cols-2 gap-2">
            {quickLinks.map((link, index) => (
              <a 
                key={index} 
                href={link.href} 
                className="text-sm font-lato hover:text-[#D2B48C] transition-colors duration-300"
              >
                {link.text}
              </a>
            ))}
          </div>
        </div>

        {/* Newsletter & Social */}
        <div className="space-y-4">
          <h4 className="text-lg font-bold font-playfair text-[#D2B48C]">Stay Connected</h4>

          {/* Newsletter */}
          <form onSubmit={handleNewsletterSubmit} className="flex">
            <input 
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder={newsletter1Placeholder}
              className="w-full px-3 py-2 bg-[#2C2C2C] text-[#F5F5F5] text-sm font-lato focus:outline-none focus:ring-2 focus:ring-[#8B4513]"
              required
            />
            <button 
              type="submit" 
              className="bg-[#8B4513] text-white px-4 py-2 text-sm font-lato hover:bg-[#D2B48C] transition-colors duration-300"
            >
              {newsletter1ButtonText}
            </button>
          </form>

          {/* Social Icons */}
          <div className="flex space-x-4 mt-4">
            {socialIcons.map((social, index) => (
              <a 
                key={index} 
                href={social.href} 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-2xl hover:text-[#D2B48C] transition-colors duration-300"
              >
                <Icon icon={social.icon} />
              </a>
            ))}
          </div>
        </div>
      </div>

      {/* Copyright */}
      <div className="text-center mt-8 pt-4 border-t border-[#2C2C2C]">
        <p className="text-sm font-lato opacity-60">
          {copyright1Text}
        </p>
      </div>
