sections.code:
({ 
  feature1Title = 'Curated Luxury Selection', 
  feature1Description,
  feature2Title = 'Vintage & Niche Perfumes', 
  feature2Description = 'Rare and collectible scents that tell a unique olfactory story',
  feature3Title = 'Expert Curation', 
  feature3Description = 'Our sommeliers of scent ensure only the most exceptional fragrances',
  feature1Icon = 'ph:star-bold',
  feature2Icon = 'ph:flask-bold',
  feature3Icon = 'ph:medal-bold'
}) => {
  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4 md:px-8 lg:px-16">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h2 className="font-playfair text-[2.75rem] text-[#5D3754] font-bold mb-4">
            Why Scent Secrets
          </h2>
          <p className="font-roboto text-[#666666] text-lg max-w-2xl mx-auto">
            Discover an unparalleled journey through the world of luxury fragrances
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-8">
          {[
            { 
              title: feature1Title, 
              description: feature1Description, 
              icon: feature1Icon 
            },
            { 
              title: feature2Title, 
              description: feature2Description, 
              icon: feature2Icon 
            },
            { 
              title: feature3Title, 
              description: feature3Description, 
              icon: feature3Icon 
            }
          ].map((feature, index) => (
            <div 
              key={index} 
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 ease-in-out transform hover:-translate-y-2"
            >
              <div className="flex items-center mb-4">
                <Icon 
                  icon={feature.icon} 
                  className="w-12 h-12 text-[#D4AF37] mr-4" 
                />
                <h3 className="font-playfair text-[1.5rem] text-[#1A2A3A] font-bold">
                  {feature.title}
                </h3>
              </div>
              <p className="font-roboto text-[#666666] text-base">
                {feature.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

({ 
  galleryItems1ImageSrc, 
  galleryItems1Title, 
  galleryItems1Description,
  galleryItems2ImageSrc, 
  galleryItems2Title, 
  galleryItems2Description,
  galleryItems3ImageSrc, 
  galleryItems3Title, 
  galleryItems3Description,
  sectionTitle,
  sectionSubtitle
}) => {
  const galleryItems = [
    {
      imageSrc: galleryItems1ImageSrc,
      title: galleryItems1Title,
      description: galleryItems1Description
    },
    {
      imageSrc: galleryItems2ImageSrc,
      title: galleryItems2Title,
      description: galleryItems2Description
    },
    {
      imageSrc: galleryItems3ImageSrc,
      title: galleryItems3Title,
      description: galleryItems3Description
    }
  ];

  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4 md:px-8 lg:px-16">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h2 className="font-playfair text-4xl text-[#5D3754] font-bold mb-4">
            {sectionTitle}
          </h2>
          <p className="font-roboto text-[#666666] text-xl max-w-2xl mx-auto">
            {sectionSubtitle}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {galleryItems.map((item, index) => (
            <div 
              key={index} 
              className="bg-white shadow-lg rounded-lg overflow-hidden transition-transform duration-300 hover:scale-105"
            >
              <div className="relative h-64 w-full">
                <img 
                  src={item.imageSrc} 
                  alt={item.title} 
                  className="absolute inset-0 w-full h-full object-cover"
                />
              </div>
              <div className="p-6">
                <h3 className="font-playfair text-2xl text-[#1A2A3A] mb-4">
                  {item.title}
                </h3>
                <p className="font-roboto text-[#2C2C2C] text-base">
                  {item.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}

({ 
