  team1Description = 'With over 15 years in luxury fragrance curation, Elena brings unparalleled expertise and passion.',
  team2ImageSrc = '/team/curator2.jpg', 
  team2Name = 'Marcus Chen', 
  team2Title = 'Lead Perfume Researcher',
  team2Description = 'A globally recognized nose with an extensive network in niche perfumery.',
  team3ImageSrc = '/team/expert3.jpg', 
  team3Name = 'Isabella Moretti', 
  team3Title = 'Global Sourcing Director',
  team3Description = 'Connecting Scent Secrets with the most exclusive fragrance houses worldwide.'
}) => {
  const teamMembers = [
    { imageSrc: team1ImageSrc, name: team1Name, title: team1Title, description: team1Description },
    { imageSrc: team2ImageSrc, name: team2Name, title: team2Title, description: team2Description },
    { imageSrc: team3ImageSrc, name: team3Name, title: team3Title, description: team3Description }
  ];

  return (
    <section className="w-full bg-[#F5F5F5] py-16 px-4 md:px-8 lg:px-16">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-center font-playfair text-3xl md:text-4xl text-[#5D3754] mb-12 font-bold">
          Meet the Scent Secrets Experts
        </h2>
        <div className="grid md:grid-cols-3 gap-8 items-stretch">
          {teamMembers.map((member, index) => (
            <div 
              key={index} 
              className="bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl group"
            >
              <div className="aspect-w-4 aspect-h-3 overflow-hidden">
                <img 
                  src={member.imageSrc} 
                  alt={member.name} 
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
