              <div className="p-6 text-center">
                <h3 className="font-playfair text-xl text-[#1A2A3A] font-bold mb-2">
                  {member.name}
                </h3>
                <p className="text-[#666666] font-roboto mb-3 text-base">
                  {member.title}
                </p>
                <p className="text-[#2C2C2C] font-roboto text-sm leading-relaxed">
                  {member.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

