({ 
  testimonial1Title, 
  testimonial1Description, 
  testimonial1AuthorName, 
  testimonial1AuthorRole,
  testimonial1AvatarSrc,
  testimonial2Title, 
  testimonial2Description, 
  testimonial2AuthorName, 
  testimonial2AuthorRole,
  testimonial2AvatarSrc,
  testimonial3Title, 
  testimonial3Description, 
  testimonial3AuthorName, 
  testimonial3AuthorRole,
  testimonial3AvatarSrc
}) => {
  const testimonials = [
    {
      title: testimonial1Title,
      description: testimonial1Description,
      author: {
        name: testimonial1AuthorName,
        role: testimonial1AuthorRole,
        avatar: testimonial1AvatarSrc
      }
    },
    {
      title: testimonial2Title,
      description: testimonial2Description,
      author: {
        name: testimonial2AuthorName,
        role: testimonial2AuthorRole,
        avatar: testimonial2AvatarSrc
      }
    },
    {
      title: testimonial3Title,
      description: testimonial3Description,
      author: {
        name: testimonial3AuthorName,
        role: testimonial3AuthorRole,
        avatar: testimonial3AvatarSrc
      }
    }
  ];

  const [currentSlide, setCurrentSlide] = React.useState(0);

  const handlePrevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? testimonials.length - 1 : prev - 1));
  };

  const handleNextSlide = () => {
    setCurrentSlide((prev) => (prev === testimonials.length - 1 ? 0 : prev + 1));
  };

  return (
    <section className="w-full bg-white py-16 px-4 md:px-8 lg:px-16">
      <div className="max-w-6xl mx-auto flex flex-col items-center">
        <h2 className="text-2xl md:text-3xl lg:text-4xl font-semibold text-center text-[#0D47A1] mb-8">
          What Our Customers Say
        </h2>

        <div className="relative w-full max-w-4xl">
          <div className="overflow-hidden">
            <motion.div 
              className="flex transition-transform duration-500"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
            >
              {testimonials.map((testimonial, index) => (
                <div 
                  key={index} 
                  className={`
                    flex-shrink-0 w-full 
                    ${index === currentSlide ? 'block' : 'hidden'}
                    flex flex-col md:flex-row items-center justify-center text-center md:text-left
                  `}
                >
                  <div className="w-32 h-32 mb-4 md:mb-0 md:mr-8 rounded-full overflow-hidden">
                    <img 
                      src={testimonial.author.avatar} 
                      alt={testimonial.author.name} 
                      className="w-full h-full object-cover"
                    />
                  </div>

                  <div className="max-w-2xl">
                    <p className="text-lg md:text-xl text-gray-700 italic mb-4">
                      "{testimonial.description}"
                    </p>
                    <div>
                      <h3 className="text-lg font-semibold text-[#0D47A1]">
                        {testimonial.author.name}
                      </h3>
                      <p className="text-sm text-gray-500">
                        {testimonial.author.role}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </motion.div>
          </div>

          <div className="flex justify-center mt-8 space-x-4">
            <button 
              onClick={handlePrevSlide}
              className="
                bg-[#0D47A1] text-white p-2 rounded-full 
                hover:bg-[#F57C00] transition-colors duration-300
                focus:outline-none focus:ring-2 focus:ring-[#4CAF50]
              "
              aria-label="Previous Testimonial"
            >
              <Icon icon="mdi:chevron-left" className="w-6 h-6" />
            </button>

            <div className="flex space-x-2">
              {testimonials.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentSlide(index)}
                  className={`
                    w-3 h-3 rounded-full 
                    ${index === currentSlide ? 'bg-[#0D47A1]' : 'bg-gray-300'}
                    hover:bg-[#F57C00] transition-colors duration-300
                  `}
                  aria-label={`Go to testimonial ${index + 1}`}
                />
              ))}
            </div>

            <button 
              onClick={handleNextSlide}
              className="
                bg-[#0D47A1] text-white p-2 rounded-full 
                hover:bg-[#F57C00] transition-colors duration-300
                focus:outline-none focus:ring-2 focus:ring-[#4CAF50]
              "
              aria-label="Next Testimonial"
            >
              <Icon icon="mdi:chevron-right" className="w-6 h-6" />
            </button>
          </div>
        </div>
      </div>
    </section>
  );
}

