({ 
  logoSrc, 
  logoAlt, 
  navLink1Text, 
  navLink1Href, 
  navLink2Text, 
  navLink2Href, 
  navLink3Text, 
  navLink3Href, 
  ctaText, 
  ctaHref 
}) => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="w-full bg-[#FFFAF0] shadow-sm">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <a href="/" className="flex items-center">
          <img 
            src={logoSrc} 
            alt={logoAlt} 
            className="h-12 w-auto object-contain"
          />
        </a>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex space-x-6 items-center">
          <a 
            href={navLink1Href} 
            className="text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-body text-base"
          >
            {navLink1Text}
          </a>
          <a 
            href={navLink2Href} 
            className="text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-body text-base"
          >
            {navLink2Text}
          </a>
          <a 
            href={navLink3Href} 
            className="text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-body text-base"
          >
            {navLink3Text}
          </a>
          <a 
            href={ctaHref} 
            className="bg-[#D32F2F] text-white px-4 py-2 rounded-md hover:bg-[#B71C1C] transition-colors duration-300 font-body"
          >
            {ctaText}
          </a>
        </nav>

        {/* Mobile Menu Toggle */}
        <button 
          onClick={toggleMenu} 
          className="md:hidden z-50 focus:outline-none"
          aria-label="Toggle mobile menu"
        >
          <span className="block w-6 h-0.5 bg-[#2C2C2C] mb-1"></span>
          <span className="block w-6 h-0.5 bg-[#2C2C2C] mb-1"></span>
          <span className="block w-6 h-0.5 bg-[#2C2C2C]"></span>
        </button>

        {/* Mobile Menu Overlay */}
        {isMenuOpen && (
          <div 
            className="fixed inset-0 bg-[#FFFAF0] z-40 flex flex-col items-center justify-center space-y-6 md:hidden"
            aria-modal="true"
            role="dialog"
          >
            <a 
              href={navLink1Href} 
              className="text-2xl text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-heading"
              onClick={toggleMenu}
            >
              {navLink1Text}
            </a>
            <a 
              href={navLink2Href} 
              className="text-2xl text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-heading"
              onClick={toggleMenu}
            >
              {navLink2Text}
            </a>
            <a 
              href={navLink3Href} 
              className="text-2xl text-[#2C2C2C] hover:text-[#D32F2F] transition-colors duration-300 font-heading"
              onClick={toggleMenu}
            >
              {navLink3Text}
            </a>
            <a 
              href={ctaHref} 
              className="bg-[#D32F2F] text-white px-6 py-3 rounded-lg text-xl hover:bg-[#B71C1C] transition-colors duration-300 font-heading"
              onClick={toggleMenu}
            >
              {ctaText}
            </a>
          </div>
        )}
      </div>
    </header>
  );
}

({ 
  heroTitle1, 
  heroSubtitle1, 
  heroCta1Href, 
  heroCta1Text,
  heroImageSrc1,
  heroImageAlt1
}) => {
  return (
    <section className="relative w-full bg-[#FFFAF0] overflow-hidden">
      <div className="container mx-auto px-4 lg:px-8 flex flex-col-reverse lg:flex-row items-center justify-between min-h-screen">
        {/* Text Content */}
        <div className="w-full lg:w-1/2 z-10 text-center lg:text-left py-16 lg:py-0">
          <h1 className="text-[#D32F2F] font-['Quattrocento'] text-4xl lg:text-5xl font-bold mb-6 leading-tight tracking-wide">
            {heroTitle1}
          </h1>
          <p className="text-[#2C2C2C] font-['Open_Sans'] text-lg lg:text-xl mb-8 leading-relaxed">
            {heroSubtitle1}
          </p>
          <a 
            href={heroCta1Href} 
            className="inline-block bg-[#D32F2F] text-white font-['Open_Sans'] font-semibold py-3 px-8 rounded-lg text-lg 
            hover:bg-[#B71C1C] transition-all duration-300 ease-in-out transform hover:scale-105 focus:outline-none 
            focus:ring-2 focus:ring-[#F4A460] focus:ring-opacity-50"
          >
            {heroCta1Text}
          </a>
        </div>

        {/* Image Content */}
        <div className="w-full lg:w-1/2 relative flex justify-center lg:justify-end mb-12 lg:mb-0">
          <div className="w-full max-w-xl aspect-w-16 aspect-h-9 rounded-xl overflow-hidden shadow-lg">
            <img 
              src={heroImageSrc1} 
              alt={heroImageAlt1}
              className="object-cover w-full h-full transform transition-transform duration-500 hover:scale-105"
            />
          </div>
        </div>
      </div>

      {/* Decorative Background Elements */}
      <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-[#F4A460] to-transparent opacity-20"></div>
    </section>
  );
}

({ 
  feature1Title, 
  feature1Description, 
  feature2Title, 
  feature2Description, 
  feature3Title, 
  feature3Description, 
  feature1IconName,
  feature2IconName, 
  feature3IconName 
}) => {
  const features = [
    {
      title: feature1Title,
      description: feature1Description,
      icon: feature1IconName
    },
    {
      title: feature2Title,
      description: feature2Description,
      icon: feature2IconName
    },
    {
      title: feature3Title,
      description: feature3Description,
      icon: feature3IconName
    }
  ];

  return (
    <section className="w-full bg-[#FFFAF0] py-16 px-4">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-center text-[2.75rem] font-quattrocento text-[#D32F2F] mb-12 font-semibold">
          Why Choose Italian Pizza
        </h2>
        <div className="grid md:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <div 
              key={index} 
              className="bg-white rounded-lg shadow-md p-6 text-center transform transition-all duration-300 hover:scale-105 hover:shadow-lg"
            >
              <div className="flex justify-center mb-4">
                <Icon 
                  icon={feature.icon} 
                  className="w-16 h-16 text-[#4CAF50]" 
                />
              </div>
              <h3 className="text-xl font-quattrocento text-[#2C2C2C] mb-3 font-semibold">
                {feature.title}
              </h3>
              <p className="text-base text-[#2C2C2C] font-opensans">
                {feature.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

({ 
  sectionTitle1, 
  sectionSubtitle1, 
  galleryItems1imageSrc, 
  galleryItems1itemTitle, 
  galleryItems1itemDescription, 
  galleryItems2imageSrc, 
  galleryItems2itemTitle, 
  galleryItems2itemDescription, 
  galleryItems3imageSrc, 
  galleryItems3itemTitle, 
  galleryItems3itemDescription 
}) => {
  const pizzaFeatures = [
    {
      icon: 'mdi:food-pizza',
      title: galleryItems1itemTitle,
      description: galleryItems1itemDescription,
      image: galleryItems1imageSrc
    },
    {
      icon: 'mdi:delivery',
      title: galleryItems2itemTitle, 
      description: galleryItems2itemDescription,
      image: galleryItems2imageSrc
    },
    {
      icon: 'mdi:chef-hat',
      title: galleryItems3itemTitle,
      description: galleryItems3itemDescription, 
      image: galleryItems3imageSrc
    }
  ];

  return (
    <section className="w-full bg-[#FFFAF0] py-16 px-4 md:px-12 lg:px-24">
      <div className="max-w-7xl mx-auto text-center">
        <h2 className="text-[#D32F2F] font-quattrocento text-3xl md:text-4xl font-bold mb-4">
          {sectionTitle1}
        </h2>
        <p className="text-lg text-gray-700 mb-12 max-w-2xl mx-auto">
          {sectionSubtitle1}
        </p>

        <div className="grid md:grid-cols-3 gap-8">
          {pizzaFeatures.map((feature, index) => (
            <motion.div 
              key={index}
              initial={{ opacity: 0, translateY: 20 }}
              whileInView={{ opacity: 1, translateY: 0 }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
              className="bg-white rounded-lg shadow-lg p-6 transform transition-all hover:scale-105"
            >
              <div className="flex justify-center mb-4">
                <Icon 
                  icon={feature.icon} 
                  className="text-[#D32F2F] text-5xl mb-4"
                />
              </div>
              <h3 className="text-xl font-semibold text-[#2C2C2C] mb-3">
                {feature.title}
              </h3>
              <p className="text-gray-600">
                {feature.description}
              </p>
              {feature.image && (
                <div className="mt-4 overflow-hidden rounded-lg">
                  <motion.img 
                    src={feature.image}
                    alt={feature.title}
                    className="w-full h-48 object-cover"
                    whileHover={{ scale: 1.1 }}
                  />
                </div>
              )}
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

