({ 
  stat1Title, 
  stat1Value, 
  stat2Title, 
  stat2Value, 
  stat3Title, 
  stat3Value,
  backgroundTitle,
  backgroundDescription
}) => {
  const statsData = [
    { 
      icon: 'mdi:pizza', 
      title: stat1Title, 
      value: stat1Value 
    },
    { 
      icon: 'mdi:truck-fast', 
      title: stat2Title, 
      value: stat2Value 
    },
    { 
      icon: 'mdi:chef-hat', 
      title: stat3Title, 
      value: stat3Value 
    }
  ];

  return (
    <section className="w-full bg-warm-cream text-rich-red py-16 md:py-24">
      <div className="container mx-auto px-4 max-w-6xl">
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-heading font-bold text-rich-red mb-4">
            {backgroundTitle}
          </h2>
          <p className="text-lg md:text-xl text-warm-sand max-w-2xl mx-auto">
            {backgroundDescription}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {statsData.map((stat, index) => (
            <motion.div 
              key={index}
              initial={{ opacity: 0, translateY: 20 }}
              whileInView={{ opacity: 1, translateY: 0 }}
              transition={{ 
                duration: 0.5, 
                delay: index * 0.2,
                ease: [0.4, 0, 0.2, 1] 
              }}
              className="bg-white rounded-lg shadow-md p-6 text-center transform transition-all hover:scale-105 hover:shadow-lg"
            >
              <div className="flex justify-center mb-4">
                <Icon 
                  icon={stat.icon} 
                  className="w-12 h-12 text-rich-red"
                />
              </div>
              <h3 className="text-xl font-semibold text-rich-red mb-2">
                {stat.title}
              </h3>
              <p className="text-3xl font-bold text-warm-sand">
                {stat.value}
              </p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
